t.script:

document.addEventListener('DOMContentLoaded', function() {
    const video = document.getElementById('webcam');
    const canvas = document.getElementById('canvas');
    const captureBtn = document.getElementById('capture');
    const checkInBtn = document.getElementById('check_in');
    const checkOutBtn = document.getElementById('check_out');
    const backBtn = document.getElementById('back_btn');
    const resultDiv = document.getElementById('result');
    let imageData = null;

    navigator.mediaDevices.getUserMedia({ video: true })
        .then(stream => {
            video.srcObject = stream;
        })
        .catch(err => {
            console.error('Camera access error:', err);
            showResult('Camera access denied. Please check permissions.', 'error');
        });

    captureBtn.addEventListener('click', function() {
        const context = canvas.getContext('2d');
        canvas.width = video.videoWidth;
        canvas.height = video.videoHeight;
        context.drawImage(video, 0, 0);
        imageData = canvas.toDataURL('image/jpeg', 0.8);

        video.style.display = 'none';
        canvas.style.display = 'block';
        checkInBtn.disabled = false;
        checkOutBtn.disabled = false;

        captureBtn.innerHTML = '<span>üîÑ</span><span>Retake</span>';
        captureBtn.addEventListener('click', resetCamera, { once: true });
    });

    checkInBtn.addEventListener('click', function() {
        if (!imageData) return;

        checkInBtn.disabled = true;
        checkOutBtn.disabled = true;
        checkInBtn.innerHTML = '<span>‚è≥</span><span>Processing...</span>';

        fetch('/attendance/check_in', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                jsonrpc: '2.0',
                method: 'call',
                params: { face_image: imageData }
            })
        })
        .then(response => response.json())
        .then(data => {
            const result = data.result;
            if (result && result.success) {
                showResult('Check-in successful!', 'success');
            } else {
                const errorMsg = result ? result.error : 'Unknown error';
                showResult('Error: ' + errorMsg, 'error');
            }
        })
        .catch(error => {
            console.error('Request error:', error);
            showResult('Connection error', 'error');
        })
        .finally(() => {
            checkInBtn.disabled = false;
            checkOutBtn.disabled = false;
            checkInBtn.innerHTML = '<span>‚úÖ</span><span>Check In</span>';
        });
    });

    checkOutBtn.addEventListener('click', function() {
        if (!imageData) return;

        checkInBtn.disabled = true;
        checkOutBtn.disabled = true;
        checkOutBtn.innerHTML = '<span>‚è≥</span><span>Processing...</span>';

        fetch('/attendance/check_out', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                jsonrpc: '2.0',
                method: 'call',
                params: { face_image: imageData }
            })
        })
        .then(response => response.json())
        .then(data => {
            const result = data.result;
            if (result && result.success) {
                showResult('Check-out successful!', 'success');
            } else {
                const errorMsg = result ? result.error : 'Unknown error';
                showResult('Error: ' + errorMsg, 'error');
            }
        })
        .catch(error => {
            console.error('Request error:', error);
            showResult('Connection error', 'error');
        })
        .finally(() => {
            checkInBtn.disabled = false;
            checkOutBtn.disabled = false;
            checkOutBtn.innerHTML = '<span>üö™</span><span>Check Out</span>';
        });
    });

    backBtn.addEventListener('click', function() {
        window.history.back();
    });

    function resetCamera() {
        video.style.display = 'block';
        canvas.style.display = 'none';
        checkInBtn.disabled = true;
        checkOutBtn.disabled = true;
        imageData = null;
        captureBtn.innerHTML = '<span>üì∑</span><span>Capture</span>';
        resultDiv.style.display = 'none';
    }

    function showResult(message, type) {
        resultDiv.textContent = message;
        resultDiv.className = `result ${type}`;
        resultDiv.style.display = 'block';
    }
});
